import React from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import { useFormik } from "formik";
import InputMask from "inputmask";

import TextInput from "../../components/TextInput";
import { BsPerson } from "react-icons/bs";
import { BiStore, BiDollar } from "react-icons/bi";
export default function DadosPessoais() {
  // Pass the useFormik() hook initial form values and a submit function that will
  // be called when the form is submitted
  const formik = useFormik({
    initialValues: {
      nameOfProduct: "",
      price: "",
    },
    onSubmit: (values) => {
      alert(JSON.stringify(values, null, 2));
    },
  });
  function setField() {}
  return (
    <div className="column space-between margin-top grow">
      <div className="form-group form-vivo">
        <InputMask
          type="text"
          className="form-control"
          id="telefone"
          required
          name="phone"
          onChange={this.setField}
          //onBlur={(e) => this.validateField(e, "phone")}
          //onFocus={() => this.clearErrorOnFocus("phone")}
          mask="[9][9][9][9][9]-99"
          value={this.state.inputData.phone}
        />

        <label htmlFor="telefone">Digite seu celular VIVO</label>
      </div>
      <form onSubmit={formik.handleSubmit}>
        <label htmlFor="nameProduct">
          <BiStore size={15} />
          Nome do Produto
        </label>
        <input
          id="nameProduct"
          name="nameProduct"
          type="text"
          onChange={formik.handleChange}
          value={formik.values.nameProduct}
        />
        <label htmlFor="price">
          <BiDollar size={15} />
          Pre√ßo
        </label>
        <input
          id="price"
          name="price"
          type="number"
          onChange={formik.handleChange}
          value={formik.values.price}
          mask="99,99"
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
